import React, { Component } from 'react'
import axios from 'axios'

class Money extends Component {
    constructor() {
        super();
        this.calcCSNAmount()
        this.state = {
            money: "8 007",
            support: "2 478"
        }
    }
    calcCSNAmount() {
        let csnMonth = new Date().getMonth() + 1;
        let currentYear = new Date().getFullYear();
        let currentDay = new Date().getDate();
        let csnDay = 24;
        console.log((new Date().getMonth()) + 1)
        if (((new Date().getMonth()) + 1) === 5) {
            this.setState(() => ({
                money: "8 007",
                support: "2 478"
            }))
        }
        else if (((new Date().getMonth()) + 1) === 1) {
            this.setState(() => ({
                money: "15 000",
                support: "5 000"
            }))
        }
        else {
            this.setState(() => ({
                money: "8 007",
                support: "2 427"
            }))
        }
        console.log((new Date().getMonth()) + 2)
        axios.get('https://api.dryg.net/dagar/v2.1/' + currentYear + '/' + csnMonth)
            .then((response) => {
                // handle success
                console.log(response);
                console.log(currentDay);
                while (("Lördag" === response.data.dagar[csnDay].veckodag) ||
                    ("Söndag" === response.data.dagar[csnDay].veckodag) ||
                    (response.data.dagar[csnDay]["röd dag"] === "Ja")) {
                    csnDay--;
                }
                if (currentDay >= (csnDay + 1)) {
                    this.setState(() => ({
                        money: "10 676",
                        support: "3 236"
                    }))
                }
            })
            .catch(function (error) {
                // handle error
                console.log(error);
            })

    }
    render() {
        return (
            <div>
                <p>Studying full time (100%) will give you <em>{this.state.money}</em> SEK, where <em>{this.state.support}</em> SEK is financial support.</p>
            </div>
        )

    }
}

export default Money
