import React, { Component } from 'react';

class Countdown extends Component {
    constructor() {
        super();
        this.state = {
            days: 23 - new Date().getDate(),
            hours: 23 - (new Date().getHours()),
            minutes: 59 - (new Date().getMinutes()),
            seconds: 59 - (new Date().getSeconds()),
            dayText: "Days",
            hourText: "Hours",
            minuteText: "Minutes",
            secondText: "Seconds",

        };
        setInterval((prevState) => {
            this.setState((prevState) => ({
                days: 23 - new Date().getDate(),
                hours: 23 - (new Date().getHours()),
                minutes: 59 - (new Date().getMinutes()),
                seconds: 59 - (new Date().getSeconds())
            }))
            if (this.state.days === 1) {
                this.setState((prevState) => ({
                    dayText: "Day"
                }))
            }
            else if (this.state.hours === 1) {
                this.setState((prevState) => ({
                    hourText: "Hour"
                }))
            }
            else if (this.state.minutes === 1) {
                this.setState((prevState) => ({
                    minuteText: "Minute"
                }))
            }
            else if (this.state.seconds === 1) {
                this.setState((prevState) => ({
                    secondText: "Second"
                }))
            }
            else {
                this.setState((prevState) => ({
                    dayText: "Days"
                }))
            }
        }, 1000);
    }
    render() {
        return (
            <p> {this.state.days + " " + this.state.dayText}  {this.state.hours + " "
                + this.state.hourText}  {this.state.minutes + " " + this.state.minuteText}  {this.state.seconds + " "
                    + this.state.secondText} </p>
        );
    }
}

export default Countdown
