                    this.setState(() => ({

                    }))
                } else {
                    this.setState(() => ({
                        days: this.state.csnDay - currentDay
                    }))
                }
            })
            .catch(function (error) {
                // handle error
                console.log(error);
            })

    }
    render() {
        return (
            <p> {this.state.days + " " + this.state.dayText}  {this.state.hours + " "
                + this.state.hourText}  {this.state.minutes + " "
                    + this.state.minuteText} {this.state.seconds + " " + this.state.secondText} </p>
        );
    }
}

