import React, { Component } from 'react';


class Countdown extends Component {
    constructor() {
        super();
        this.state = {
            days: 23 - new Date().getDate(),
            hours: 23 - (new Date().getHours()),
            minutes: 59 - (new Date().getMinutes()),
            seconds: 59 - (new Date().getSeconds()),
            dayText: "Days",
            hourText: "Hours",
            minuteText: "Minutes",
            secondText: "Seconds",
        };
        const csnDay = 25;
        setInterval((prevState) => {

            if (!this.isItCSN()) {
                this.setState(() => ({
                    days: 23 - new Date().getDate(),
                    hours: 23 - (new Date().getHours()),
                    minutes: 59 - (new Date().getMinutes()),
                    seconds: 59 - (new Date().getSeconds())
                }))
                if (this.state.days === 1) {
                    this.setState(() => ({
                        dayText: "Day"
                    }))
                }
                else if (this.state.hours === 1) {
                    this.setState(() => ({
                        hourText: "Hour"
                    }))
                }
                else if (this.state.minutes === 1) {
                    this.setState(() => ({
                        minuteText: "Minute"
                    }))
                }
                else if (this.state.seconds === 1) {
                    this.setState(() => ({
                        secondText: "Second"
                    }))
                }
                else {
                    this.setState(() => ({
                        dayText: "Days",
                        hourText: "Hours",
                        minuteText: "Minutes",
                        secondText: "Seconds",
                    }))
                }
            }
            else {
                this.setState(() => ({
                    days: "",
                    hours: "",
                    minutes: "",
                    seconds: "",
                    dayText: "It's CSN time!!",
                    hourText: "",
                    minuteText: "",
                    secondText: "",
                }))

            }
        }, 1000);

    }
    isItCSN() {
        console.log(59 - new Date().getMinutes())
        if ((59 - new Date().getMinutes()) === 35) {
            return true;
        }
        return false;
    }
    render() {
        return (
            <p> {this.state.days + " " + this.state.dayText}  {this.state.hours + " "
                + this.state.hourText}  {this.state.minutes + " " + this.state.minuteText}  {this.state.seconds + " "
                    + this.state.secondText} </p>
        );
    }
}

export default Countdown
