import React, { Component } from 'react';
import Countdown from './Countdown.js'
import Confetti from 'react-confetti'
import './App.css';
import axios from 'axios';


class App extends Component {
  constructor() {
    super();
    this.state = {
      quote: "",
      quoter: ""
    }
  }
  componentDidMount() {
    axios.get('http://localhost:3002/quote/3')

      .then(function (response) {
        // handle success
        console.log(response);
        console.log(response.data[0].Qu_Quote)
        this.setState((prevState) => ({
          quote: response.data[0].Qu_Quote,
          quoter: response.data[0].Qu_Quoter
        }))

      })
      .catch(function (error) {
        // handle error
        console.log(error);
      })
  }

  render() {
    return (
      <div className="App" >
        <Confetti numberOfPieces="350" run={false} />
        <div className="intro-text">
          <p>When is CSN due?</p>
          <hr />
        </div>
        <div className="timer">
          <Countdown />
        </div>
        <div className="qoute-of-the-day">
          <div>
            <p className="quote">"{this.state.quote}"</p>
            <p className="quoter">- {this.state.quoter}</p>
          </div>
        </div>
        <div className="link-csn">
          <a href="https://www.csn.se/" target="_blank"> <br /> Click here to visit CSNs website</a>
          <div className="jonathan">
            <p>By Jonathan Hedlund</p>
            <a href="https://jonathanhedlund.com/" target="_blank">www.jonathanhedlund.com</a>
          </div>
        </div>
      </div>
    );
  }

}

export default App;
