import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor() {
    super();
    this.state = {
      days: 1,
      hours: 23,
      minutes: 0,
      seconds: new Date().getSeconds()
    };
    setInterval((prevState) => {
      if (this.state.seconds === 0 && this.state.minutes === 0 && this.state.hours === 0) {
        this.setState((prevState) => ({
          seconds: 59,
          minutes: 59,
          hours: 23,
          days: prevState.days - 1
        }))
      }
      if (this.state.seconds === 0 && this.state.minutes === 0) {
        this.setState((prevState) => ({
          seconds: 59,
          minutes: 59,
          hours: prevState.hours - 1
        }))
      }
      else if (this.state.seconds === 0) {
        this.setState((prevState) => ({
          seconds: 59,
          minutes: prevState.minutes - 1
        }))
      }
      else {
        this.setState((prevState) => ({
          seconds: prevState.seconds - 1
        }))
      }
    }, 1000);
  }
  render() {
    return (
      <div className="App" >
        <div className="intro-text">
          <p>When is CSN due?</p>
          <hr />
        </div>
        <div className="timer">
          <p> {this.state.days} Days {this.state.hours} Hours {this.state.minutes} Minutes {this.state.seconds} Seconds</p>
        </div>
        <div className="qoute-of-the-day">
          <div>
            <p className="quote">"People say nothing is impossible, but i do nothing every day"</p>
            <p className="quoter">- Winnie T. Pooh</p>
          </div>
        </div>
        <div className="link-csn">
          <a href="https://www.csn.se/" target="_blank"> <br /> Click here to visit CSNs website</a>
          <div className="jonathan">
            <p>By Jonathan Hedlund</p>
            <a href="https://jonathanhedlund.com/" target="_blank">www.jonathanhedlund.com</a>
          </div>
        </div>
      </div>
    );
  }

}

export default App;
