import React, { Component } from 'react';

class Countdown extends Component {
    constructor() {
        super();
        this.state = {
            days: 23 - new Date().getDate(),
            hours: 23 - (new Date().getHours()),
            minutes: 59 - (new Date().getMinutes()),
            seconds: 59 - (new Date().getSeconds()),
            dayText: "Days",
            hourText: "Hours",
            minuteText: "Minutes",
            secondText: "Seconds",

        };
        setInterval((prevState) => {
            this.setState((prevState) => ({
                days: 23 - new Date().getDate(),
                hours: 23 - (new Date().getHours()),
                minutes: 59 - (new Date().getMinutes()),
                seconds: 59 - (new Date().getSeconds())
            }))
        }, 1000);
    }
    render() {
        return (
            <p> {this.state.days + " " + this.state.dayText}  {this.state.hours} Hours {this.state.minutes} Minutes {this.state.seconds} Seconds</p>
        );
    }
}

export default Countdown
