import React, { Component } from 'react';
import axios from 'axios'


class Countdown extends Component {
    constructor() {
        super();
        const csnDay = this.calcCSNDay();
        this.state = {
            days: "",
            hours: 23 - (new Date().getHours()),
            minutes: 59 - (new Date().getMinutes()),
            seconds: 59 - (new Date().getSeconds()),
            dayText: "Days",
            hourText: "Hours",
            minuteText: "Minutes",
            secondText: "Seconds",
        };
        //const csnDay = this.calcCSNDay();
        setInterval((prevState) => {
            if (!this.isItCSN()) {
                this.setState(() => ({

                    hours: 23 - (new Date().getHours()),
                    minutes: 59 - (new Date().getMinutes()),
                    seconds: 59 - (new Date().getSeconds())
                }))
                if (this.state.days === 1) {
                    this.setState(() => ({
                        dayText: "Day"
                    }))
                }
                else if (this.state.hours === 1) {
                    this.setState(() => ({
                        hourText: "Hour"
                    }))
                }
                else if (this.state.minutes === 1) {
                    this.setState(() => ({
                        minuteText: "Minute"
                    }))
                }
                else if (this.state.seconds === 1) {
                    this.setState(() => ({
                        secondText: "Second"
                    }))
                }
                else {
                    this.setState(() => ({
                        dayText: "Days",
                        hourText: "Hours",
                        minuteText: "Minutes",
                        secondText: "Seconds",
                    }))
                }
            }
            else {
                this.setState(() => ({
                    days: "",
                    hours: "",
                    minutes: "",
                    seconds: "",
                    dayText: "It's CSN time!!",
                    hourText: "",
                    minuteText: "",
                    secondText: "",
                }))

            }
        }, 1000);

    }
    isItCSN() {
        console.log(59 - new Date().getMinutes())
        if ((59 - new Date().getMinutes()) === 35) {
            return true;
        }
        return false;
    }
    //Calculates which date CSN comes
    calcCSNDay() {
        let csnMonth = new Date().getMonth();
        let currentYear = new Date().getFullYear();
        let currentDay = "";
        let csnDay = 24;
        console.log((new Date().getMonth()) + 1)
        axios.get('https://api.dryg.net/dagar/v2.1/2019/' + (new Date().getMonth() + 3))
            .then((response) => {
                // handle success
                console.log(currentDay);
                console.log(response);
                while (("Lördag" === response.data.dagar[csnDay].veckodag) ||
                    ("Söndag" === response.data.dagar[csnDay].veckodag) ||
                    (response.data.dagar[csnDay]["röd dag"] === "Ja")) {
                    csnDay--;
                }
                this.setState(() => ({
                    days: csnDay - (new Date().getDate())
                }))
            })
            .catch(function (error) {
                // handle error
                console.log(error);
            })

    }
    render() {
        return (
            <p> {this.state.days + " " + this.state.dayText}  {this.state.hours + " "
                + this.state.hourText}  {this.state.minutes + " " + this.state.minuteText} {this.state.seconds + " " + this.state.secondText} </p>
        );
    }
}

export default Countdown
